/* Function print spaceship oled*/
static void print_spaceship(void) {
        /* this fixes the screen on and off bug */
    if (current_wpm > 0) {
        oled_on();
        anim_sleep = timer_read32();
    } else if(timer_elapsed32(anim_sleep) > OLED_TIMEOUT) {
        /* clear */
        oled_set_cursor(0,0);
        oled_write("                                                                                                    ", false);
        oled_off();
        return;
    }

    /* Render bullets */
    render_bullets();

    /*Render space*/
    render_space();

    /* Print current layer */
    oled_set_cursor(0, 14);
    oled_write("LAYER", false);

    oled_set_cursor(0, 15);
    switch (get_highest_layer(layer_state)) {
        case _BASE:
            oled_write("Base ", false);
            break;
        case _NAV:
            oled_write("Navi ", false);
            break;
        case _SYM:
            oled_write("Sym  ", false);
            break;
        case _MOUSE:
            oled_write("Mouse", false);
            break;
        case _QWERTY:
            oled_write("QWRTY", false);
            break;
        case _NUM:
            oled_write("Num  ", false);
            break;
        case _UTIL:
            oled_write("Util ", false);
            break;
        default:
            oled_write("Undef", false);
    }
}

oled_rotation_t oled_init_user(oled_rotation_t rotation) { return OLED_ROTATION_270; }

bool oled_task_user(void) {
    /* Get current WPM */
    current_wpm   = get_current_wpm();
    led_usb_state = host_keyboard_led_state();

    /* Print oled screen */
    if (!is_caps_word_on()) {
        print_spaceship();
    } else {
        print_rubber_duck();
    }
    return false;
}

#endif


bool process_record_user(uint16_t keycode, keyrecord_t *record) {
    switch (keycode) {
        /* KEYBOARD SPACESHIP STATUS START */
        case KC_W:
        case KC_K:
        case KC_UP:
            if (record->event.pressed) {
                if(y_spaceship > MIN_SPACESHIP_DIM) {
                    y_spaceship--;
                }
            }
            break;
        case KC_S:
        case KC_J:
        case KC_DOWN:
            if (record->event.pressed) {
                if(y_spaceship < MAX_SPACESHIP_DIM) {
                    y_spaceship++;
                }
            }
            break;
        case KC_A:
        case KC_H:
        case KC_LEFT:
            if (record->event.pressed) {
                if(x_spaceship > LEFT_SPACESHIP_DIM) {
                    x_spaceship--;
                }
            }
            break;
        case KC_D:
        case KC_L:
        case KC_RGHT:
            if (record->event.pressed) {
                if(x_spaceship < RIGHT_SPACESHIP_DIM) {
                    x_spaceship++;
                }
            }
            break;

        /* Fire when pressing space*/
        case KC_SPC:
            if (record->event.pressed) {
                for (int i = 0; i < MAX_BULLETS; i++) {
                    if(bullets[i] == -1) {
                        bullets[i] = y_spaceship - 1;
                        break;
                    }
                }
            }
            break;
        /* KEYBOARD SPACESHIP STATUS END */
    }
    return true;
}
/* KEYBOARD SPACESHIP END */

